package patterns;

public class Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int star=1,n=5;
		for(int i=1;i<=n;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print("*");
			}
			System.out.println();
			star++;
			
		}
		
		
		

	}

}
